-- Query Name: qryAutismIndividualsAndFamilyForDDS
-- Extracted: 2026-02-04 13:04:22 (ADO Method)

SELECT tblPeople_1.LastName AS ClientLastName, tblPeople_1.FirstName AS ClientFirstName, tblPeople_1.MiddleInitial AS ClientMiddleInitial, tblPeople_1.PhysicalAddress AS ClientPhysicalAddress, tblPeople_1.PhysicalCity AS ClientPhysicalCity, tblPeople_1.PhysicalState AS ClientPhysicalState, tblPeople_1.PhysicalZIP AS ClientPhysicalZIP, tblPeople_1.HomePhone AS ClientHomePhone, tblPeople_1.WorkPhone AS ClientWorkPhone, tblPeople_1.MobilePhone AS ClientMobilePhone, tblPeople_1.EmailAddress AS ClientEmailAddress, tblPeopleClientsDemographics.Gender AS ClientGender, tblPeopleClientsDemographics.DateOfBirth AS ClientDateOfBirth, tblPeopleClientsDemographics.Age AS ClientAge, tblPeopleClientsAutismServices.GeneralDiagnosis AS ClientGeneralDiagnosis, tblPeopleClientsAutismServices.Diagnosis AS ClientDiagnosis, tblPeople.LastName AS FamilyLastName, tblPeople.FirstName AS FamilyFirstName, tblPeople.MiddleInitial AS FamilyMiddleInitial, tblPeople.PhysicalAddress AS FamilyPhysicalAddress, tblPeople.PhysicalCity AS FamilyPhysicalCity, tblPeople.PhysicalState AS FamilyPhysicalState, tblPeople.PhysicalZIP AS FamilyPhysicalZIP, tblPeople.HomePhone AS FamilyHomePhone, tblPeople.WorkPhone AS FamilyWorkPhone, tblPeople.MobilePhone AS FamilyMobilePhone, tblPeople.EmailAddress AS FamilyEmailAddress
FROM (((tblPeople INNER JOIN tblPeopleFamily ON tblPeople.IndexedName = tblPeopleFamily.IndexedName) INNER JOIN tblPeople AS tblPeople_1 ON tblPeopleFamily.ClientIndexedName = tblPeople_1.IndexedName) INNER JOIN tblPeopleClientsDemographics ON tblPeople_1.IndexedName = tblPeopleClientsDemographics.IndexedName) INNER JOIN tblPeopleClientsAutismServices ON tblPeople_1.IndexedName = tblPeopleClientsAutismServices.IndexedName
WHERE (((tblPeople_1.IsClientAutism)=True) AND ((tblPeople_1.IsDeceased)=False) AND ((tblPeople_1.NoMailings)=False) AND ((tblPeople.IsFamilyGuardian)=True) AND ((tblPeople.IsDeceased)=False) AND ((tblPeople.NoMailings)=False))
ORDER BY tblPeople_1.LastName, tblPeople_1.FirstName;

