SELECT tblContracts.FY, tblContractsBillingBook.AccountingStaff, IIf(Left([tblContracts]![ContractID],1)="1" Or Left([tblContracts]![ContractID],2)="IN","DDS","Non-DDS") AS DDS, tblContracts.ContractID, tblContractsBillingBook.BIllingBookNumber, tblContracts.ActivityCode, tblContracts.GPNumber, tblContracts.State, tblContracts.UnitRate, tblContracts.Units AS ContractUnits, tblContracts.MaximumObligation AS ContractMaximumObligation, tblContracts.MaximumObligationAsAmended, tblContracts.TotalClients, tblContracts.TotalUnits, tblContracts.TotalUnitsAsAmended, tblContractsBillingBook.ProgramName, tblContractsBillingBook.CostCenter, tblContractsBillingBook.MaximumObligationAsAmended AS BillingBookMaximumObligation, tblContractsBillingBook.UnitsAsAmended AS BillingBookUnits, tblContractsBillingBook.BillingRate, tblContractsBillingBook.NumberOfClients, tblContractsBillingBook.InternalRate, tblContractsBillingBook.FundingSource, tblContractsBillingBook.DDSArea, tblContractsBillingBook.Staff, tblContractsBillingBook.Comments AS BillingBookComments, [BillingRate]*[NumberOfClients]*[Forms]![frmRptFinancialAndLetters]![MRNumDays] AS BilledPerProgram, [InternalRate]*[NumberOfClients]*[Forms]![frmRptFinancialAndLetters]![MRNumDays] AS InternalAmount
FROM tblContracts LEFT JOIN tblContractsBillingBook ON (tblContracts.FY = tblContractsBillingBook.FY) AND (tblContracts.ContractID = tblContractsBillingBook.ContractID)
WHERE (((tblContracts.FY)=Forms!frmRptFinancialAndLetters!MRFY) And ((tblContractsBillingBook.BIllingBookNumber)>=400 And (tblContractsBillingBook.BIllingBookNumber)<=599))
ORDER BY IIf(Left([tblContracts]![ContractID],1)="1" Or Left([tblContracts]![ContractID],2)="IN","DDS","Non-DDS"), tblContracts.ContractID, tblContractsBillingBook.BIllingBookNumber;

